mapper.select:
SELECT ASEQ, SIGUN_NM, DATE_FORMAT(RECEPT_DE, '%Y/%m/%d') AS RECEPT_DE, DISCVRY_PLC_INFO, STATE_NM, PBLANC_IDNTFY_NO, PBLANC_BEGIN_DE,PBLANC_END_DE,SPECIES_NM,COLOR_NM,AGE_INFO,BDWGH_INFO,SEX_NM,NEUT_YN, SFETR_INFO, SHTER_NM, SHTER_TELNO, PROTECT_PLC, REFINE_ROADNM_ADDR, REFINE_LOTNO_ADDR, REFINE_ZIP_CD, JURISD_INST_NM, IMAGE_COURS FROM ANIMALS
AND SIGUN_CD = #{sortRegion} and SPECIES_NM like concat('%', #{sortSpecies}, '%')
AND SIGUN_CD = #{sortRegion}
AND SPECIES_NM like concat('%', #{sortSpecies}, '%')
ORDER BY ASEQ DESC LIMIT #{pagination.limitStart}, #{recordSize}
